import React, { Component } from "react";
import "./components.css";
import "./footer.css";
import Typography from "@material-ui/core/Typography";

import Fade from "react-reveal/Fade";
import Flip from "react-reveal/Flip";
import Zoom from "react-reveal/Zoom";
import Slide from "react-reveal/Slide";
import ReactSimpleTooltip from "react-simple-tooltip";
import { Link, animateScroll as scroll } from "react-scroll";
import axios from "axios";

// https://ciunkos.com/creating-contact-forms-with-nodemailer-and-react
export default class Footer extends Component {
  handleSubmit(e) {
    e.preventDefault();
    const name = document.getElementById("name").value;
    const email = document.getElementById("email").value;
    const message = document.getElementById("message").value;
    axios({
      method: "POST",
      url: "http://localhost:3002/send",
      data: {
        name: name,
        email: email,
        messsage: message
      }
    }).then(response => {
      if (response.data.msg === "success") {
        alert("Message Sent.");
        this.resetForm();
      } else if (response.data.msg === "fail") {
        alert("Message failed to send.");
      }
    });
  }

  resetForm() {
    document.getElementById("contact-form").reset();
  }
  render() {
    return (
      <div>
        <div className="footer" id="contact">
          <div className="center5">
            <Typography variant="h3" className="gentlepadding">
              <Fade bottom>CONTACT</Fade>
              <Fade bottom>
                <div className="subhead" />
              </Fade>
            </Typography>
            {/* <form
              method="POST"
              className="flex2"
              id="contact-form"
              onSubmit={this.handleSubmit.bind(this)}
            >
              <Fade bottom>
                <input
                  id="name"
                  type="text"
                  placeholder="Name"
                  className="footername"
                />
              </Fade>
              <Fade bottom delay="200">
                <input
                  id="email"
                  type="email"
                  placeholder="Email"
                  className="footeremail"
                />
              </Fade>

              <Fade bottom delay="200">
                <textarea
                  id="message"
                  placeholder="Message"
                  rows="4"
                  cols="35"
                  className="footermessage"
                />
              </Fade>
              <Fade bottom delay="600">
                <button type="submit" className="button">
                  Submit
                </button>
              </Fade>
            </form> */}

            <div className="icons">
              <ReactSimpleTooltip
                arrow={15}
                background="#000"
                border="#000"
                color="#fff"
                content="703-577-6388"
                fadeDuration={0}
                fadeEasing="linear"
                fixed={false}
                fontFamily="inherit"
                fontSize="inherit"
                offset={0}
                padding={10}
                placement="top"
                radius={0}
                zIndex={1}
              >
                <Flip left delay="400">
                  <div className="icon1">
                    <a href="tel:7035776388">
                      <img
                        src={require("../images/phone.png")}
                        className="icon"
                      />
                    </a>
                  </div>
                </Flip>
              </ReactSimpleTooltip>

              <div className="iconpadding" />

              <Flip left delay="600">
                <div className="icon1">
                  <a href="mailto:sp3bk@virginia.edu">
                    <img
                      src={require("../images/mail4.png")}
                      className="icon"
                    />
                  </a>
                </div>
              </Flip>

              <div className="iconpadding" />
              <Flip left delay="800">
                <div className="icon1">
                  <a href="https://www.linkedin.com/in/sung-joon-park/">
                    <img
                      src={require("../images/linkedin2.png")}
                      className="icon"
                    />
                  </a>
                </div>
              </Flip>

              <div className="iconpadding" />
              <Flip left delay="1000">
                <div className="icon1">
                  <a href="http://github.com/sjp1136/">
                    <img
                      src={require("../images/github2.svg")}
                      className="icon"
                    />
                  </a>
                </div>
              </Flip>
            </div>
            <Link to="home" spy={true} smooth={true} duration={2000}>
              <div className="button2">BACK TO HOME</div>
            </Link>
            <div className="copyright">&copy;2019 SUNG JOON PARK</div>
          </div>
        </div>
      </div>
      // </div>
    );
  }
}
